var FormRepeater = function () {
    return {
        init: function () {
            $(".mt-repeater").each(function () {
                initEmpty:true,
                $(this).repeater({
                    initEmpty: true,
                    show: function () {
                        $(this).slideDown();
                       $('.summernote').summernote();
                    }, hide: function (e) {
                        confirm("Are you sure you want to delete this element?") && $(this).slideUp(e)
                    }, ready: function (e) {
                    }
                })
                $(this).find('[data-repeater-item]').first().remove();
            })
        }
    }
}();
jQuery(document).ready(function () {
    FormRepeater.init()
});
